import React, {useState} from "react";
import {CreationForms} from "./components/CreationForms";
import {ShowTodos} from "./components/ShowTodos";
import {getTasks} from "../Domain/Tasks";
import {createTask} from "../App/CreateTask";
import {removeTask} from "../App/RemoveTask";

function App() {
    const [tasks, setTasks] = useState(getTasks());

    // Rafraîchit la liste des tâches depuis le domaine
    const refreshTasks = () => {
        setTasks(getTasks());
    };

    // Callback métier pour la création
    const handleCreate = (title: string, description?: string, deadlineDate?: Date) => {
        createTask(title, description, deadlineDate);
        refreshTasks();
    };

    // Callback métier pour la suppression
    const handleDelete = (title: string) => {
        removeTask(title);
        refreshTasks();
    };

    return (
        <div style={{ maxWidth: '1200px', margin: '0 auto', padding: '32px 24px' }}>
            <CreationForms onCreate={handleCreate} />
            <ShowTodos tasks={tasks} onDelete={handleDelete} />
        </div>
    )
}

export default App;

import React, {useState} from "react";
import {CreationForms} from "./components/CreationForms";
import {ShowTodos} from "./components/ShowTodos";
import {getTasks} from "../Domain/Tasks";
import {createTask} from "../App/CreateTask";
import {removeTask} from "../App/RemoveTask";

function App() {
    const [tasks, setTasks] = useState(getTasks());

    // Rafraîchit la liste des tâches depuis le domaine
    const refreshTasks = () => {
        setTasks(getTasks());
    };

    // Callback métier pour la création
    const handleCreate = (title: string, description?: string, deadlineDate?: Date) => {
        createTask(title, description, deadlineDate);
        refreshTasks();
    };

    // Callback métier pour la suppression
    const handleDelete = (title: string) => {
        removeTask(title);
        refreshTasks();
    };

    // Callback métier pour la mise à jour
    const handleUpdate = () => {
        refreshTasks();
    };

    return (
        <div style={{ maxWidth: '1200px', margin: '0 auto', padding: '32px 24px' }}>
            <CreationForms onCreate={handleCreate} />
            <ShowTodos tasks={tasks} onDelete={handleDelete} onUpdate={handleUpdate} />
        </div>
    )
}

export default App;

import React from "react";
import {TaskComponent} from "./TaskComponent";
import {Task} from "../../Domain/Task";

export function ShowTodos({tasks, onDelete}: {tasks: Task[], onDelete: (title: string) => void}) {
    return (
        <div className="table-container">
            <h2 style={{marginBottom: '18px'}}>Liste des Todos</h2>
            <table>
                <thead>
                <tr>
                    <th>Titre</th>
                    <th>Description</th>
                    <th>Date limite</th>
                    <th>Status</th>
                    <th>Actions</th>
                </tr>
                </thead>
                <tbody>
                    {tasks.map(task => (
                        <TaskComponent key={task.uuid} task={task} onDelete={onDelete}/>
                    ))}
                </tbody>
            </table>
        </div>
    )
}

import React from "react";
import {TaskComponent} from "./TaskComponent";
import {Task} from "../../Domain/Task";

export function ShowTodos({tasks, onDelete, onUpdate}: {tasks: Task[], onDelete: (title: string) => void, onUpdate: () => void}) {
    return (
        <div className="table-container">
            <h2 style={{marginBottom: '18px'}}>Liste des Todos</h2>
            <table>
                <thead>
                <tr>
                    <th>Titre</th>
                    <th>Description</th>
                    <th>Date limite</th>
                    <th>Status</th>
                    <th>Actions</th>
                </tr>
                </thead>
                <tbody>
                    {tasks.map(task => (
                        <TaskComponent key={task.uuid} task={task} onDelete={onDelete} onUpdate={onUpdate}/>
                    ))}
                </tbody>
            </table>
        </div>
    )
}

import {Task} from "../../Domain/Task";
import {StateType, StateConfig} from "../../Domain/StateType";
import React, {useState} from "react";
import {editDeadlineDate, editDescription, editTitle} from "../../App/EditTask";
import {getTask} from "../../Domain/Tasks";

export function TaskComponent({task, onDelete}: { task: Task, onDelete: (title: string) => void }) {
    const [localState, setLocalState] = useState<StateType>(task.state);
    const [editField, setEditField] = useState<null | 'title' | 'description' | 'deadline'>(null);
    const [editValue, setEditValue] = useState<string>("");
    const [showStatusDropdown, setShowStatusDropdown] = useState(false);

    function handleClickDelete(e: React.MouseEvent<HTMLButtonElement>) {
        e.preventDefault();
        onDelete(task.title);
    }

    function handleEdit(field: 'title' | 'description' | 'deadline') {
        setEditField(field);
        if (field === 'deadline') {
            setEditValue(task.deadlineDate ? task.deadlineDate.toISOString().slice(0, 10) : "");
        } else {
            setEditValue((task as any)[field] ?? "");
        }
    }

    function handleSave(title : string, field: 'title' | 'description' | 'deadline') {
        // À compléter par la logique métier
        const editedTask : Task | undefined = getTask(title)
        if (!editedTask) return;
        if (field === 'title') {
            editTitle(editedTask, editField);
        } else if (field === 'description') {
            editDescription(editedTask, editField);
        } else if (field === 'deadline') {
            editDeadlineDate(editedTask, editField ? new Date(editField) : undefined);
        }
    }

    function handleStatusChange(newStatus: StateType) {
        setLocalState(newStatus);
        setShowStatusDropdown(false);
    }

    const currentStatusConfig = StateConfig[localState];

    return (
        <tr className="task-row" key={task.uuid}>
            {/* Titre */}
            <td>
                {editField === 'title' ? (
                    <div style={{display: 'flex', alignItems: 'center', gap: '8px'}}>
                        <input
                            type="text"
                            value={editValue}
                            onChange={e => setEditValue(e.target.value)}
                            className="edit-input"
                            style={{width: '100%'}}
                        />
                        <button className="icon-btn" onClick={() => handleSave(task.title, 'title')} title="Sauvegarder">
                            <span style={{fontSize: '1em'}}>✔️</span>
                        </button>
                    </div>
                ) : (
                    <div style={{display: 'flex', alignItems: 'center', gap: '8px'}}>
                        <span style={{flex: 1}}>{task.title}</span>
                        <button className="icon-btn" onClick={() => handleEdit('title')} title="Modifier le titre">
                            <span style={{fontSize: '1em', opacity: 0.7}}>✏️</span>
                        </button>
                    </div>
                )}
            </td>
            {/* Description */}
            <td>
                {editField === 'description' ? (
                    <div style={{display: 'flex', alignItems: 'center', gap: '8px'}}>
                        <textarea
                            value={editValue}
                            onChange={e => setEditValue(e.target.value)}
                            className="edit-input"
                            style={{resize: 'vertical', minHeight: '24px', width: '100%'}}
                        />
                        <button className="icon-btn" onClick={() => handleSave(task.title, 'description')} title="Sauvegarder">
                            <span style={{fontSize: '1em'}}>✔️</span>
                        </button>
                    </div>
                ) : (
                    <div style={{display: 'flex', alignItems: 'center', gap: '8px'}}>
                        <span style={{flex: 1}}>{task.description}</span>
                        <button className="icon-btn" onClick={() => handleEdit('description')} title="Modifier la description">
                            <span style={{fontSize: '1em', opacity: 0.7}}>✏️</span>
                        </button>
                    </div>
                )}
            </td>
            {/* Deadline */}
            <td>
                {editField === 'deadline' ? (
                    <div style={{display: 'flex', alignItems: 'center', gap: '8px'}}>
                        <input
                            type="date"
                            value={editValue}
                            onChange={e => setEditValue(e.target.value)}
                            className="edit-input"
                            style={{width: '120px'}}
                        />
                        <button className="icon-btn" onClick={() => handleSave(task.title, 'deadline')} title="Sauvegarder">
                            <span style={{fontSize: '1em'}}>✔️</span>
                        </button>
                    </div>
                ) : (
                    <div style={{display: 'flex', alignItems: 'center', gap: '8px'}}>
                        <span style={{flex: 1}}>{task.deadlineDate ? task.deadlineDate.toLocaleDateString('fr-FR', { day: 'numeric', month: 'long', year: 'numeric' }) : "—"}</span>
                        <button className="icon-btn" onClick={() => handleEdit('deadline')} title="Modifier la date limite">
                            <span style={{fontSize: '1em', opacity: 0.7}}>✏️</span>
                        </button>
                    </div>
                )}
            </td>
            {/* Statut visuel avec badge coloré */}
            <td>
                <div className="status-dropdown">
                    <button
                        className={`status-badge ${currentStatusConfig.class}`}
                        onClick={() => setShowStatusDropdown(!showStatusDropdown)}
                        onBlur={() => setTimeout(() => setShowStatusDropdown(false), 200)}
                        title="Changer le statut"
                    >
                        <span>{currentStatusConfig.icon}</span>
                        <span>{currentStatusConfig.label}</span>
                        <span style={{fontSize: '0.7em', opacity: 0.6}}>▼</span>
                    </button>
                    {showStatusDropdown && (
                        <div className="status-options">
                            {Object.values(StateType).map(status => {
                                const config = StateConfig[status];
                                return (
                                    <button
                                        key={status}
                                        className="status-option"
                                        onClick={() => handleStatusChange(status)}
                                    >
                                        <span>{config.icon}</span>
                                        <span>{config.label}</span>
                                    </button>
                                );
                            })}
                        </div>
                    )}
                </div>
            </td>
            {/* Actions minimalistes */}
            <td style={{textAlign: 'center', width: '32px'}}>
                <button className="icon-btn delete" onClick={handleClickDelete} title="Supprimer">
                    <span style={{fontSize: '1em', opacity: 0.8}}>️</span>
                </button>
            </td>
        </tr>
    );
}

import {Task} from "../../Domain/Task";
import {StateType, StateConfig} from "../../Domain/StateType";
import React, {useState} from "react";
import {editDeadlineDate, editDescription, editTitle, editStatus} from "../../App/EditTask";
import {getTask} from "../../Domain/Tasks";

export function TaskComponent({task, onDelete, onUpdate}: { task: Task, onDelete: (title: string) => void, onUpdate: () => void }) {
    const [localState, setLocalState] = useState<StateType>(task.state);
    const [editField, setEditField] = useState<null | 'title' | 'description' | 'deadline'>(null);
    const [editValue, setEditValue] = useState<string>("");
    const [showStatusDropdown, setShowStatusDropdown] = useState(false);

    function handleClickDelete(e: React.MouseEvent<HTMLButtonElement>) {
        e.preventDefault();
        onDelete(task.title);
    }

    function handleEdit(field: 'title' | 'description' | 'deadline') {
        setEditField(field);
        if (field === 'deadline') {
            setEditValue(task.deadlineDate ? task.deadlineDate.toISOString().slice(0, 10) : "");
        } else {
            setEditValue((task as any)[field] ?? "");
        }
    }

    function handleSave(title : string, field: 'title' | 'description' | 'deadline') {
        // À compléter par la logique métier
        const editedTask : Task | undefined = getTask(title)
        if (!editedTask) return;
        if (field === 'title') {
            editTitle(editedTask, editValue);
        } else if (field === 'description') {
            editDescription(editedTask, editValue);
        } else if (field === 'deadline') {
            editDeadlineDate(editedTask, editValue ? new Date(editValue) : undefined);
        }

        // Réinitialiser l'état d'édition
        setEditField(null);
        setEditValue("");

        // Actualiser le tableau
        onUpdate();
    }

    function handleStatusChange(newStatus: StateType) {
        const editedTask = getTask(task.title);
        if (editedTask) {
            editStatus(editedTask, newStatus);
            onUpdate();
        }
        setLocalState(newStatus);
        setShowStatusDropdown(false);
    }

    const currentStatusConfig = StateConfig[localState];

    return (
        <tr className="task-row" key={task.uuid}>
            {/* Titre */}
            <td>
                {editField === 'title' ? (
                    <div style={{display: 'flex', alignItems: 'center', gap: '8px'}}>
                        <input
                            type="text"
                            value={editValue}
                            onChange={e => setEditValue(e.target.value)}
                            className="edit-input"
                            style={{width: '100%'}}
                        />
                        <button className="icon-btn" onClick={() => handleSave(task.title, 'title')} title="Sauvegarder">
                            <span style={{fontSize: '1em'}}>✔️</span>
                        </button>
                    </div>
                ) : (
                    <div style={{display: 'flex', alignItems: 'center', gap: '8px'}}>
                        <span style={{flex: 1}}>{task.title}</span>
                        <button className="icon-btn" onClick={() => handleEdit('title')} title="Modifier le titre">
                            <span style={{fontSize: '1em', opacity: 0.7}}>✏️</span>
                        </button>
                    </div>
                )}
            </td>
            {/* Description */}
            <td>
                {editField === 'description' ? (
                    <div style={{display: 'flex', alignItems: 'center', gap: '8px'}}>
                        <textarea
                            value={editValue}
                            onChange={e => setEditValue(e.target.value)}
                            className="edit-input"
                            style={{resize: 'vertical', minHeight: '24px', width: '100%'}}
                        />
                        <button className="icon-btn" onClick={() => handleSave(task.title, 'description')} title="Sauvegarder">
                            <span style={{fontSize: '1em'}}>✔️</span>
                        </button>
                    </div>
                ) : (
                    <div style={{display: 'flex', alignItems: 'center', gap: '8px'}}>
                        <span style={{flex: 1}}>{task.description}</span>
                        <button className="icon-btn" onClick={() => handleEdit('description')} title="Modifier la description">
                            <span style={{fontSize: '1em', opacity: 0.7}}>✏️</span>
                        </button>
                    </div>
                )}
            </td>
            {/* Deadline */}
            <td>
                {editField === 'deadline' ? (
                    <div style={{display: 'flex', alignItems: 'center', gap: '8px'}}>
                        <input
                            type="date"
                            value={editValue}
                            onChange={e => setEditValue(e.target.value)}
                            className="edit-input"
                            style={{width: '120px'}}
                        />
                        <button className="icon-btn" onClick={() => handleSave(task.title, 'deadline')} title="Sauvegarder">
                            <span style={{fontSize: '1em'}}>✔️</span>
                        </button>
                    </div>
                ) : (
                    <div style={{display: 'flex', alignItems: 'center', gap: '8px'}}>
                        <span style={{flex: 1}}>{task.deadlineDate ? task.deadlineDate.toLocaleDateString('fr-FR', { day: 'numeric', month: 'long', year: 'numeric' }) : "—"}</span>
                        <button className="icon-btn" onClick={() => handleEdit('deadline')} title="Modifier la date limite">
                            <span style={{fontSize: '1em', opacity: 0.7}}>✏️</span>
                        </button>
                    </div>
                )}
            </td>
            {/* Statut visuel avec badge coloré */}
            <td>
                <div className="status-dropdown">
                    <button
                        className={`status-badge ${currentStatusConfig.class}`}
                        onClick={() => setShowStatusDropdown(!showStatusDropdown)}
                        onBlur={() => setTimeout(() => setShowStatusDropdown(false), 200)}
                        title="Changer le statut"
                    >
                        <span>{currentStatusConfig.icon}</span>
                        <span>{currentStatusConfig.label}</span>
                        <span style={{fontSize: '0.7em', opacity: 0.6}}>▼</span>
                    </button>
                    {showStatusDropdown && (
                        <div className="status-options">
                            {Object.values(StateType).map(status => {
                                const config = StateConfig[status];
                                return (
                                    <button
                                        key={status}
                                        className="status-option"
                                        onClick={() => handleStatusChange(status)}
                                    >
                                        <span>{config.icon}</span>
                                        <span>{config.label}</span>
                                    </button>
                                );
                            })}
                        </div>
                    )}
                </div>
            </td>
            {/* Actions minimalistes */}
            <td style={{textAlign: 'center', width: '32px'}}>
                <button className="icon-btn delete" onClick={handleClickDelete} title="Supprimer">
                    <span style={{fontSize: '1em', opacity: 0.8}}>️</span>
                </button>
            </td>
        </tr>
    );
}
