import tasks from "../../Domain/Tasks";
import {TaskComponent} from "./TaskComponent";

export function ShowTodos() {
    return (
        <div className="show-todos">
            <h2>Liste des Todos</h2>

            <table>
                <thead>
                <tr>
                    <th>Titre</th>
                    <th>Description</th>
                    <th>Date limite</th>
                    <th>Status</th>
                    <th>Actions</th>
                </tr>
                </thead>
                <tbody>
                    {tasks.map(task => (
                        <TaskComponent key={task.id} task={task}/>
                    ))}
                </tbody>
            </table>
        </div>
    )
}

import {Task} from "../../Domain/Task";

export function TaskComponent({task}: { task: Task }) {
    return (
        <tr className="task-row">
            <td>{task.title}</td>
            <td>{task.description}</td>
            <td>
                <button
                    className={`button${task.isDone ? ' done' : ''}`}
                    onClick={() => task.isDone = !task.isDone}
                >
                    {task.isDone ? "Terminé" : "En attente"}
                </button>
            </td>
            <td>{task.createdAt.toLocaleString()}</td>
            {task.deadlineDate && (
                <td>{task.deadlineDate.toLocaleString()}</td>
            )}
            <td>
                <button className="button" onClick={() => alert("WIP ...")}>Modifier</button>
                <button className="button delete" onClick={() => alert("WIP ...")}>Supprimer</button>
            </td>
        </tr>
    );
}

import {Task} from "../../Domain/Task";

export function TaskComponent({task}: { task: Task }) {
    return (
        <tr className="task-row">
            <td>{task.title}</td>
            <td>{task.description}</td>
            <td>{task.deadlineDate ? task.deadlineDate.toLocaleString() : "—"}</td>
            <td>
                <button
                    className={`button${task.isDone ? ' done' : ''}`}
                    onClick={() => task.isDone = !task.isDone}
                >
                    {task.isDone ? "Terminé" : "En attente"}
                </button>
            </td>
            <td>
                <button className="button" onClick={() => alert("WIP ...")}>Modifier</button>
                <button className="button delete" onClick={() => alert("WIP ...")}>Supprimer</button>
            </td>
        </tr>
    );
}
